function bootstrao_grid_detect(){
    var winWidth =  $(window).width();

    if(winWidth < 544 ){
        gusto.bootstrap['grid'] = 'xs';
    }else if( winWidth <= 767){
        gusto.bootstrap['grid'] = 'sm';
    }else if( winWidth <= 991){
        gusto.bootstrap['grid'] = 'md';
    }else if( winWidth <= 1199){
        gusto.bootstrap['grid'] = 'lg';
    }else if( winWidth >= 1200){
        gusto.bootstrap['grid'] = 'xl';
    }
}



