function mobile_state(){
    gusto.opt.mobile = false;
    gusto.opt.tablet = false;
    gusto.opt.smart  = false;
    gusto.opt.type   = false;
    gusto.opt.rotation = false;
    if($('.global_tablet_mob').css('display') == 'none'){
        gusto.opt.mobile = true;
        gusto.opt.tablet = true;
    }
    if($('.global_smart_mob').css('display') == 'none'){
        gusto.opt.mobile = true;
        gusto.opt.tablet = true;
        gusto.opt.smart = true;
    }
    if($('html').hasClass('mobile')){
        gusto.opt.type   = 'mobile';
        gusto.opt.mobile = true;
        gusto.opt.tablet = false;
        gusto.opt.smart  = true;
    }
    if($('html').hasClass('tablet')){
        gusto.opt.type   = 'tablet';
        gusto.opt.mobile = true;
        gusto.opt.tablet = true;
        gusto.opt.smart  = false;
    }
    if($('html').hasClass('desktop')){
        gusto.opt.type   = 'desktop';
        gusto.opt.mobile = false;
        gusto.opt.tablet = false;
        gusto.opt.smart  = false;
    }
    if($('html').hasClass('landscape')){
        gusto.opt.rotation   = 'landscape';
    }
    if($('html').hasClass('portrait')){
        gusto.opt.rotation   = 'portrait';
    }
}




