$(gusto).on({
    "resize": function(){

    },
    "loader::hide_complete": function(){

        $('.animIn').each(function(){
            //var index = $(this).data('index');
            var $trigger = $(this);
            var result = verge.inViewport($trigger,"0");
            if(result){
                if(!$(this).hasClass('animation-enter')){
                    $(this).addClass('animation-enter');
                    TweenLite.to($(this),.35, {opacity: 1, y: "0",scale:"1",force3D:!0});
                }

            }
        });
    },
    "nav::open" : function(){
        TweenLite.to($('.claim_header'),.3,{opacity:0});
        TweenLite.to($('.back-page-btn'),.3,{opacity:0,delay:.1});
    },
    "nav::close" : function(){
        if(!animation_from_hidden){
            TweenLite.to($('.claim_header'),.3,{opacity:1});
            TweenLite.to($('.back-page-btn'),.3,{opacity:1,delay:.1});
        }

    },
    "loader::showClick": function(){

        if(animation_from_hidden) {
            TweenMax.staggerTo($('.pagination a'),1,{ opacity: 0},.1);
            TweenMax.staggerTo($('.linee_bck span').not('[data-module="nav"] .linee_bck span'), 1.5,{y:'-100%',background: 'rgba(0, 0, 0, .15)',ease: Expo.easeOut}, .1);
            TweenLite.to('.cont_prod,.big_title,.claim_header,.next_post_row,.module-inclusion-text-col,.scroll_down_line,.cont_article',1,{opacity:0});
            TweenLite.to(window,1.2, {scrollTo:0,ease: Power4.easeInOut, onComplete: function(){
                prod_enter_tl.reverse().timeScale(2.1);
            }});
        }
    }
});

function resize(){
    $('#main').css('min-height',$(window).height());
    if($('.global_tablet_mob').css('display') == 'none'){
        mobile = true;
        $('.full_height.col-intro-img').css('height',$(window).height());
        $('.full_height.col-intro-text,.full_height.col-intro-text').css('height',$(window).height() *.5);
    }else{
        mobile = false;
        $('.full_height').css('height',$(window).height());
    }
    if(!gusto.opt.smart) {
        $('[data-type="even_odd"] .row .col-img').each(function(){
            $(this).closest('.row').attr('data-random',getRandomArbitrary(1.01,1.5));
            var imgH = $(this).find('img').height();
            $(this).find('.col-cont').height(imgH-120);
        });
    }else {
        $('[data-type="even_odd"] .row .col-img').find('.col-cont').height('auto');
    }

    if($('.scroll_down_line').length > 0) {
        if (($('body').height() - 200) <= $(window).height()) {
            $('.scroll_down_line').css('display','none');
        }else{
            $('.scroll_down_line').css('display','block');
        }
    }
    if($('.archive_material_cat').length > 0){
        if($('.page_content').height() < $(window).height()){
            $('.module-matrix-alternate-row').css('height', $(window).height()-$('.next_post_row').height());
        }
    }
}

